import React from "react";
import { useSelector} from 'react-redux';
import {
  selectGoods
} from '../store/goodsSlice';
import {
  selectCart
} from '../store/cartSlice';

import Cart from "../components/Cart";

function CartList() {
  const goods = useSelector(selectGoods);
  const cart = useSelector(selectCart);
  //Переіндeксація масиву
  const goodsObj = goods.reduce((accum, item) => {
    accum[item['articul']] = item;
    return accum;
  }, {});
  console.log('Переіндексований масив', goodsObj);
  return (
    <div>
      
        
          {/* // Object.keys(cart).map(item => 
          //   <li key={item + goodsObj[item]['title']}>
          //     {goodsObj[item]['title']}-{cart[item]}
          //   </li> */}
      
      <table className="table-users">
        <tbody>
          <tr>
            <th>Goods_name</th><th>Price</th><th>Quantity</th><th>Amount</th><th>Delete</th>
          </tr>
          <tr>
            {Object.keys(cart).map(item =>
              <Cart title={goodsObj[item]['title']} cost={goodsObj[item]['cost']} quantity={cart[item]} articul={item.articul}/>)} 
                       
          </tr>
           
        </tbody>
      </table>

           
         
    </div>
  )
}
export default CartList;
