import React from "react";
import { useSelector, useDispatch} from 'react-redux';
import {
  selectGoods
} from '../store/goodsSlice';
import {
  selectCart
} from '../store/cartSlice';

import Cart from "../components/Cart";
import { decrement } from "../store/cartSlice";

function CartList() {
  const goods = useSelector(selectGoods);
  const cart = useSelector(selectCart);
  const dispatch = useDispatch();
  //Переіндeксація масиву
  const goodsObj = goods.reduce((accum, item) => {
    accum[item['articul']] = item;
    return accum;
  }, {});
  console.log('Переіндексований масив', goodsObj);

  const clickHandler = (event) => {
     event.preventDefault();
    let elemBtnMinus = event.target;
    let elemBtnPlus
    if (!elemBtnMinus.classList.contains('btn-minus')) return true;
    dispatch(decrement(elemBtnMinus.getAttribute('data-key')));   
  }
  
  return (
    <div>
      <h2>Basket</h2>
       <table >
        <thead>
          <tr>
            <th>Title</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Amount</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody onClick={clickHandler}>  
          {Object.keys(cart).map(item =>
              <Cart
                key={item + goodsObj[item]['title']}
                title={goodsObj[item]['title']}
                image={goodsObj[item]['image']}
                price={goodsObj[item]['cost']}
                quantity={cart[item]}
                articul={item} />
            )} 
        </tbody>
        <tfoot>
          <tr>
            <td colSpan='3'>Total:</td>
            <td colSpan='2'>0</td>
          </tr>
        </tfoot>            
      </table>
    
          {/* // Object.keys(cart).map(item => 
          //   <li key={item + goodsObj[item]['title']}>
          //     {goodsObj[item]['title']}-{cart[item]}
          //   </li> */}
              
   </div>
  )
}
export default CartList;
