    remove: (state, data) => {
      console.log('dddddd',data);
      let articul = data.payload;
     
    }
  }
  
});
export const { increment, minus, remove } = cartSlice.actions;
export const selectCart = state => state.cart.value;
export default cartSlice.reducer;
